import MuiTextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios'
import { styled } from '@mui/system';
import { useState } from 'react'
import { Grid, IconButton, Typography } from '@mui/material';
import { Box } from '@mui/system';
import { Avatar } from '@mui/material';
import { useRef, useEffect } from 'react';
import AutoCompleteInput from './AutoCompleteInput'
import Chip from '@mui/material/Chip';
import Collapse from '@mui/material/Collapse';
import WorkExperienceForm from "./WorkExperienceForm";
import AddIcon from '@mui/icons-material/Add';
import CloseIcon from '@mui/icons-material/Close';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import EditIcon from '@mui/icons-material/Edit';
import SkillsForm from './SkillsForm'
import InterestsForm from './InterestsForm';

//import {useForm, Controller} from "react-hook-form"

const TextField = styled(MuiTextField)({
  width: '100%'
});
const ListItem = styled('li')(({ theme }) => ({
  margin: theme.spacing(0.5),
  float: 'left',
}));




const ProfileForm = ({ user }) => {
  const [profile, setProfile] = useState(null);
  const [image, _setImage] = useState(null);
  const profileImg = useRef(null)
  const [firstName, setFirstName] = useState(user?.firstName ?? '')
  const [lastName, setLastName] = useState(user?.lastName ?? '')




  const [editLinks, setEditLinks] = useState(false)

  const [showWorkExperienceForm, setShowWorkExperienceForm] = useState(false);
  useEffect(() => {
    axios.get('/profile/getByUserId/' + user._id).then(res => {
      setProfile(res.data)
    })
  }, [])

  useEffect(() => {
    if (profile?.profile_picture) {
      let img = profile.profile_picture
      const buf = Buffer.from(img.data)
      let imgSrc = buf.toString('utf8');
      setImage(imgSrc)
    }
    // _setImage(profile.profile_picture)
  }, [profile])
  const cleanup = () => {
    URL.revokeObjectURL(image);
    profileImg.current.value = null;
  };

  const setImage = (newImage) => {
    if (image) {
      cleanup();
    }
    _setImage(newImage);
  };

  const handleOnChange = (event) => {
    const newImage = event.target?.files?.[0];
    if (newImage) {

      var reader = new FileReader();
      reader.onloadend = function () {
        console.log(reader.result)
        axios.put('/profile/updateProfilePicture', { _id: profile._id, profile_picture: reader.result }).then(res => {
          console.log(res)
        })
      }
      reader.readAsDataURL(newImage);


      // newImage.text().then(img => {
      //   console.log(img)

      // })
      setImage(URL.createObjectURL(newImage));
    }


  };
  const onProfilePictureClick = () => {
    profileImg.current.click();
  };

  //add value to list
  const clickAddButton = (value, setValue, setValues) => {
    setValues(values => {
      if (values && value && !values.includes(value))
        return values.concat(value)
      return values
    })
    setValue('')
  }

  const handleDelete = (chipToDelete, setter) => () => {
    setter((chips) => chips.filter((chip) => chip !== chipToDelete));
  };
  return (
    <>
      <Box sx={{ maxWidth: '50%' }}>
        <Grid
          container
          justifyContent="center"
          alignItems="center"
          rowSpacing={2}
          columnSpacing={2}
        >
          <Grid item xs={12} container justifyContent="center">
            <IconButton onClick={onProfilePictureClick}>
              <input type='file' id='file' ref={profileImg} onChange={handleOnChange} style={{ display: 'none' }} />
              <Avatar
                sx={{ width: 150, height: 150 }}
                alt="Name"
                src={image || "/broken-image.jpg"}
              >
                User
              </Avatar>
            </IconButton>
          </Grid>
          <Grid item xs={12} container justifyContent="center" sx={{ marginBottom: "2em" }}>
            <Typography variant="h6"> {user?.email} </Typography>
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="First Name"
              variant="outlined"
              value={firstName}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Last Name"
              variant="outlined"
              value={lastName}
            />
          </Grid>
          {/************************************SKILLS************************************/}
          <Grid item xs={12}>
            <SkillsForm />
          </Grid>
          {/**********************************Interests***********************************/}
          <Grid item xs={12}>
            <InterestsForm />
          </Grid>

          {/**********************************Projects***********************************/}
          <Grid item xs={10}>
            <Typography variant="h6">Project/Work Experience</Typography>
          </Grid>
          <Grid item xs={2}>
            <Button onClick={() => setShowWorkExperienceForm((prev) => !prev)}>
              {!showWorkExperienceForm ? <AddIcon /> : <CloseIcon />}
            </Button>
          </Grid>
          <Grid item xs={12}>
            <Collapse in={showWorkExperienceForm}>
              <WorkExperienceForm setShowWorkExperienceForm={setShowWorkExperienceForm} />
            </Collapse>
          </Grid>

          {/**********************************Links***********************************/}
          <Grid item xs={10} >
            <Typography variant="h6" sx={{ paddingBottom: "0px" }}>Link</Typography>
          </Grid>
          <Grid item xs={2}>
            <Button onClick={() => setEditLinks((prev) => !prev)}>
              {!editLinks ? <EditIcon /> : <CloseIcon />}
            </Button>
          </Grid>
          <Grid item xs={12}>
            <Collapse in={editLinks}>
              <Grid
                container
                justifyContent="center"
                alignItems="center"
                rowSpacing={2}
                columnSpacing={2}
              >

                <Grid item xs={1}>
                  <LinkedInIcon color="primary" fontSize="large" />
                </Grid>
                <Grid item xs={11} sx={{ paddingBottom: "20px" }}>
                  <TextField
                    label="Linked In"
                    variant="standard"
                    value={""}
                  />
                </Grid>
                <Grid item xs={1}>
                  <GitHubIcon fontSize="large" />
                </Grid>
                <Grid item xs={11} sx={{ paddingBottom: "20px" }}>
                  <TextField
                    label="Git Hub"
                    variant="standard"
                    value={""}
                  />
                </Grid>
                <Grid item xs={12} container justifyContent="right">
                  <Button variant="text">Save</Button>
                </Grid>
              </Grid>
            </Collapse>
          </Grid>
        </Grid>
      </Box>
    </>

  );
};



export default ProfileForm
