import { makeStyles } from '@material-ui/core';
import MuiTextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios'
import { styled } from '@mui/system';
import { useState } from 'react'
import { Grid, IconButton, Typography } from '@mui/material';
import { Box } from '@mui/system';
import { Avatar } from '@mui/material';
import { useRef, useEffect } from 'react';
import AutoCompleteInput from './AutoCompleteInput'
import Chip from '@mui/material/Chip';
import Collapse from '@mui/material/Collapse';
import WorkExperienceForm from "./WorkExperienceForm";
import Switch from '@mui/material/Switch';
import AddIcon from '@mui/icons-material/Add';
import CloseIcon from '@mui/icons-material/Close';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import EditIcon from '@mui/icons-material/Edit';

const skillOptions = ['HTML', 'CSS', 'Javascript', 'Node', 'React', 'MongoDB'];
const TextField = styled(MuiTextField)({
  width: '100%'
});
const ListItem = styled('li')(({ theme }) => ({
  margin: theme.spacing(0.5),
  float: 'left',
}));


const SkillsForm = () => {

  const [editSkills, setEditSkills] = useState(false)
  const [skill, setSkill] = useState('');
  const [skills, setSkills] = useState([])
  const addSkillBtnRef = useRef()

  const clickAddButton = (value, setValue, setValues) => {
    setValues(values => {
      if (values && value && !values.includes(value))
        return values.concat(value)
      return values
    })
    setValue('')
  }

  const handleDelete = (chipToDelete, setter) => () => {
    setter((chips) => chips.filter((chip) => chip !== chipToDelete));
  };

  return (
    <>
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        rowSpacing={2}
        columnSpacing={2}
      >
      <Grid item xs={10}>
        <Typography variant="h6">Skills </Typography>
      </Grid>
      <Grid item xs={2}>
        <Button onClick={() => setEditSkills((prev) => !prev)}>
          {!editSkills ? <EditIcon /> : <CloseIcon />}
        </Button>
      </Grid>
      <Grid item xs={12}>
        <Collapse in={editSkills}>
          <Grid
            container
            justifyContent="center"
            alignItems="center"
            rowSpacing={2}
            columnSpacing={2}
          >
            <Grid item xs={10}>
              <AutoCompleteInput
                selectLabel={"Skills"}
                optionList={skillOptions}
                value={skill}
                setValue={setSkill}
                buttonRef={addSkillBtnRef} />
            </Grid>
            <Grid item xs={2}>
              <Button variant="text" ref={addSkillBtnRef} onClick={() => clickAddButton(skill, setSkill, setSkills)}>ADD</Button>
            </Grid>

            <Grid item xs={12} container sx={{ listStyle: 'none' }}>
              {skills.map(skill => {
                return (
                  <ListItem key={skill}>
                    <Chip
                      label={skill}
                      onDelete={handleDelete(skill, setSkills)}
                    />
                  </ListItem>
                )
              })}
            </Grid>
            <Grid item xs={12} container justifyContent="right">
              <Button variant="text">Save</Button>
            </Grid>
          </Grid>
        </Collapse>
      </Grid>
    </Grid>
    </>
  )
}


export default SkillsForm
