import { makeStyles } from '@material-ui/core';
import MuiTextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios'
import { styled } from '@mui/system';
import { useState } from 'react'
import { Grid, IconButton } from '@mui/material';
import { Box } from '@mui/system';
import { Avatar } from '@mui/material';
import { useRef } from 'react';
import AutoCompleteInput from './AutoCompleteInput'
import Chip from '@mui/material/Chip';

//import {useForm, Controller} from "react-hook-form"

const TextField = styled(MuiTextField)({
  width: '100%'
});
const ListItem = styled('li')(({ theme }) => ({
  margin: theme.spacing(0.5),
}));



const ProfileForm = ({ handleClose }) => {

  const [image, _setImage] = useState(null);
  const profileImg = useRef(null)
  const [skill, setSkill] = useState('');
  const [skills, setSkills] = useState([])
  const addSkillBtnRef = useRef()

  const cleanup = () => {
    URL.revokeObjectURL(image);
    profileImg.current.value = null;
  };

  const setImage = (newImage) => {
    if (image) {
      cleanup();
    }
    _setImage(newImage);
  };

  const handleOnChange = (event) => {
    const newImage = event.target?.files?.[0];
    if (newImage) {
      setImage(URL.createObjectURL(newImage));
      //fetch new image
    }
  };
  const onProfilePictureClick = () => {
    profileImg.current.click();
  };

  //add value to list
  const clickAddButton = (value, setValue, setValues) => {
    setValues(values => {
      // if(!values.includes(value))
        values.concat(value)
    })
    setValue('')
  }

  const handleDelete = (chipToDelete, setter) => () => {
    setter((chips) => chips.filter((chip) => chip !== chipToDelete));
  };
  return (
    <>
      <Box sx={{ maxWidth: '70%' }}>
        <Grid
          container
          justifyContent="center"
          alignItems="center"
          rowSpacing={2}
          columnSpacing={2}
        >
          <Grid item xs={12} container justifyContent="center">
            <IconButton onClick={onProfilePictureClick}>
              <input type='file' id='file' ref={profileImg} onChange={handleOnChange} style={{ display: 'none' }} />
              <Avatar
                sx={{ width: 120, height: 120 }}
                alt="Name"
                src={image || "/broken-image.jpg"}
              >
                User
              </Avatar>
            </IconButton>
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="First Name"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Last Name"
              variant="outlined"
              fullWidth
            />
          </Grid>
          <Grid item xs={9}>
            <AutoCompleteInput value={skill} setValue={setSkill} buttonRef={addSkillBtnRef} />
          </Grid>
          <Grid item xs={3}>
            <Button variant="text" ref={addSkillBtnRef} onClick={() => clickAddButton(skill, setSkill, setSkills)}>ADD</Button>
          </Grid>
          <Grid item xs={12} sx={{display:'flex', listStyle: 'none'}}>
            {skills?.map(skill => {
              return (
                <ListItem key={skill}>
                  <Chip
                    label={skill}
                    onDelete={handleDelete(skill, setSkills)}
                  />
                </ListItem>
                )
            })}
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Title"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Title"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Title"
              variant="outlined"
              fullWidth

            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Title"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              label="Title"
              variant="outlined"
            />

          </Grid>
        </Grid>
      </Box>
    </>

  );
};



export default ProfileForm
